import { DefaultTheme } from "styled-components";

export const theme: DefaultTheme = {
  colors: {
    black: "#202125",
    white: "#FFFFFF",
    green: "#5BCB02",
    grey1: "#F8F8F8",
    grey2: "#EEEEEE",
    grey3: "#A1A2A4",
  },
  spacing: {
    spacing1: "0.4rem",
    spacing2: "0.6rem",
    spacing3: "0.8rem",
    spacing4: "1rem",
  },
  fontStyles: {
    regular: {
      "font-size": "1.6rem",
      "line-height": "2.2rem",
      "font-weight": "400",
    },
    regularSmall: {
      "font-size": "1.4rem",
      "line-height": "1.8rem",
      "font-weight": "400",
    },
    regularMedium: {
      "font-size": "1.6rem",
      "line-height": "2.2rem",
      "font-weight": "500",
    },
    bold: {
      "font-size": "2.4rem",
      "line-height": "3.0rem",
      "font-weight": "bold",
    },
    smallBold: {
      "font-size": "1.2rem",
      "line-height": "1.6rem",
      "font-weight": "bold",
    },
  },
};
